var fs = require('fs');
var config = require('config');

function getFiles (){
    this.files_ =  [];
    this.dir = config.inputPath;
    var files = fs.readdirSync(this.dir);
    for (var i in files){
        var name = files[i];
        this.files_.push(name);
    }
}

exports.getFiles = getFiles;